function RevealMask({
    id,
    revealImage,
    position,
    onDragStart,
    isActive,
    maskScale,
    maskOffset,
    svgPath,
    svgStyle,
    minHandleSize,
    debugHandles,
}) {
    const maskId = `reveal-mask-${id}`
    const pathRef = React.useRef(null)
    const [handleSize, setHandleSize] = React.useState({
        width: 50,
        height: 50,
    })
    const { offsetX = 0, offsetY = 0 } = maskOffset || {}
    const {
        borderWidth = 0,
        borderColor = "#000",
        shadowBlur = 0,
        shadowColor = "#000",
        shadowOffsetX = 0,
        shadowOffsetY = 0,
    } = svgStyle || {}

    React.useEffect(() => {
        if (pathRef.current) {
            const bbox = pathRef.current.getBBox()
            const scaleFactor = maskScale / 16
            const width = bbox.width * scaleFactor
            const height = bbox.height * scaleFactor

            const finalWidth = Math.max(width, minHandleSize)
            const finalHeight = Math.max(height, minHandleSize)

            setHandleSize({
                width: finalWidth,
                height: finalHeight,
            })
        }
    }, [maskScale, svgPath, minHandleSize])

    return (
        <div
            style={{
                ...styles.revealLayer,
                backgroundImage: `url(${revealImage?.src || ""})`,
                WebkitMaskImage: `url(#${maskId})`,
                maskImage: `url(#${maskId})`,
            }}
        >
            <div
                style={{
                    ...styles.hitArea,
                    left: position.x,
                    top: position.y,
                    width: `${handleSize.width}px`,
                    height: `${handleSize.height}px`,
                    cursor: isActive ? "grabbing" : "grab",
                    ...(debugHandles && {
                        border: "2px solid red",
                        backgroundColor: "rgba(255, 0, 0, 0.1)",
                    }),
                }}
                onMouseDown={(e) => {
                    e.preventDefault()
                    onDragStart(e, id)
                }}
                onTouchStart={(e) => {
                    e.preventDefault()
                    const touch = e.touches[0]
                    onDragStart(touch, id)
                }}
            />
            <svg style={styles.maskSvg} width="100%" height="100%">
                <defs>
                    <mask id={maskId}>
                        <rect width="100%" height="100%" fill="black" />
                        <g
                            transform={`translate(${position.x}, ${position.y}) scale(${maskScale}) translate(${-8 + offsetX}, ${-2 + offsetY})`}
                        >
                            <path
                                ref={pathRef}
                                d={SHAPES[svgPath] || SHAPES.triangle}
                                fill="white"
                                style={{
                                    stroke: borderColor,
                                    strokeWidth: borderWidth,
                                    filter: `drop-shadow(${shadowOffsetX}px ${shadowOffsetY}px ${shadowBlur}px ${shadowColor})`,
                                }}
                            />
                        </g>
                    </mask>
                </defs>
            </svg>
        </div>
    )
}

export default function RevealWindow(props) {
    const containerRef = React.useRef(null)
    const [positions, setPositions] = React.useState(
        Array(8).fill({ x: 0, y: 0 })
    )
    const [activeIndex, setActiveIndex] = React.useState(null)
    const dragOffset = React.useRef({ x: 0, y: 0 })

    React.useEffect(() => {
        if (containerRef.current) {
            const { width, height } =
                containerRef.current.getBoundingClientRect()
            const newPositions = props.initialPositions.map((pos) => ({
                x: width * (pos.x / 100),
                y: height * (pos.y / 100),
            }))
            setPositions(newPositions)
        }
    }, [props.initialPositions])

    const handleDragStart = (event, index) => {
        if (!containerRef.current) return

        const rect = containerRef.current.getBoundingClientRect()
        const currentPos = positions[index]

        dragOffset.current = {
            x: event.clientX - rect.left - currentPos.x,
            y: event.clientY - rect.top - currentPos.y,
        }

        const handleMove = (moveEvent) => {
            const clientX = moveEvent.clientX || moveEvent.touches?.[0]?.clientX
            const clientY = moveEvent.clientY || moveEvent.touches?.[0]?.clientY

            const rect = containerRef.current.getBoundingClientRect()
            const x = clientX - rect.left - dragOffset.current.x
            const y = clientY - rect.top - dragOffset.current.y

            const newX = Math.min(Math.max(0, x), rect.width)
            const newY = Math.min(Math.max(0, y), rect.height)

            setPositions((prev) => {
                const newPositions = [...prev]
                newPositions[index] = { x: newX, y: newY }
                return newPositions
            })
        }

        const handleEnd = () => {
            document.removeEventListener("mousemove", handleMove)
            document.removeEventListener("mouseup", handleEnd)
            document.removeEventListener("touchmove", handleMove)
            document.removeEventListener("touchend", handleEnd)
            setActiveIndex(null)
        }

        setActiveIndex(index)
        document.addEventListener("mousemove", handleMove)
        document.addEventListener("mouseup", handleEnd)
        document.addEventListener("touchmove", handleMove)
        document.addEventListener("touchend", handleEnd)
    }

    const activeRevealLayers = React.useMemo(() => {
        return Array.from({ length: 8 })
            .map((_, index) => ({
                config: props[`reveal${index + 1}`],
                index,
            }))
            .filter(({ config }) => config?.image)
    }, [
        props.reveal1,
        props.reveal2,
        props.reveal3,
        props.reveal4,
        props.reveal5,
        props.reveal6,
        props.reveal7,
        props.reveal8,
    ])

    return (
        <div ref={containerRef} style={styles.container}>
            <div
                style={{
                    ...styles.baseImage,
                    ...(props.baseImage?.src
                        ? { backgroundImage: `url(${props.baseImage.src})` }
                        : { backgroundColor: props.baseColor }),
                }}
            />
            {activeRevealLayers.map(({ config, index }) => (
                <RevealMask
                    key={index}
                    id={index}
                    revealImage={config.image}
                    position={positions[index]}
                    onDragStart={handleDragStart}
                    isActive={activeIndex === index}
                    maskScale={config.maskScale}
                    maskOffset={config.maskOffset}
                    svgPath={config.svgPath}
                    svgStyle={config.svgStyle}
                    minHandleSize={config.minHandleSize}
                    debugHandles={props.debugHandles}
                />
            ))}
        </div>
    )
}

